"use strict";
var config_1 = require('./util/config');
var fs_extra_1 = require('fs-extra');
var logger_1 = require('./util/logger');
function clean(context) {
    context = config_1.generateContext(context);
    var logger = new logger_1.Logger('clean');
    try {
        logger_1.Logger.debug("clean " + context.wwwDir);
        fs_extra_1.emptyDirSync(context.wwwDir);
        logger.finish();
    }
    catch (e) {
        logger.fail(e, "Error cleaning " + context.wwwDir + ", " + e);
        throw e;
    }
}
exports.clean = clean;
